SELECT p.privilege_name,p.parent_privilege,a.uuid,a.role_id,p.privilege_id,a.type,a.function1 FROM (SELECT * FROM s_sys_privilege WHERE PRIVILEGE_TYPE = 1) p LEFT JOIN (SELECT * FROM s_sys_roleprvg
role_id=#role_id#
) a ON p.privilege_id = a.privilege_id Order By p.MENU_PRIORITY,p.LM_TIME

